export const OS = (props) => {
  const { title } = props;

  if (title.includes('windows')) {
    return <Windows width={props.width} height={props.height} title={title} />;
  } else if (title.includes('ubuntu')) {
    return <Ubuntu width={props.width} height={props.height} title={title} />;
  } else if (title.includes('android')) {
    return <Android width={props.width} height={props.height} title={title} />;
  } else if (title.includes('ios')) {
    return <IOS width={props.width} height={props.height} title={title} />;
  }

  return null;
}
