export const ReadOrDelete = ({ title }) => {
  const [isHovered, setIsHovered] = useState(false);
  return (
    <Tooltip title={title}>
      <div
        onMouseEnter={() => setIsHovered(true)}
        onMouseLeave={() => setIsHovered(false)}
        className='cursor-pointer '
      >
        {isHovered ? <CloseCircleOutlined className="text-[#FF0000] ml-2" style={{ fontSize: 18 }} /> :
          <CheckCircleOutlined className="text-[#50f774] ml-2" style={{ fontSize: 18 }} />
        }
      </div>
    </Tooltip>
  );
}


export const BrowserIcon = (props) => {
  let src = null;

  if (props.title.includes("chrome")) {
    src = `${API_BASE_URL}browser_icons/chrome.png`;
  } else if (props.title.includes("firefox")) {
    src = `${API_BASE_URL}browser_icons/firefox.png`;
  } else if (props.title.includes("safari")) {
    src = `${API_BASE_URL}browser_icons/safari.png`;
  } else if (props.title.includes("edge")) {
    src = `${API_BASE_URL}browser_icons/edge.png`;
  } else if (props.title.includes("brave")) {
    src = `${API_BASE_URL}browser_icons/brave.png`;
  } else if (props.title.includes("opera")) {
    src = `${API_BASE_URL}browser_icons/opera.png`;
  }

  return src ? <img src={src} alt="browser" width={props.width} height={props.height} /> : null;
};

